import React, { useState, useEffect, createRef } from "react";
import styled from "styled-components";
import { makeStyles } from "@material-ui/core/styles";
import AppBar from "@material-ui/core/AppBar";
import Toolbar from "@material-ui/core/Toolbar";
import Typography from "@material-ui/core/Typography";
import Button from "@material-ui/core/Button";
import IconButton from "@material-ui/core/IconButton";
import MenuIcon from "@material-ui/icons/Menu";
import Grid from '@material-ui/core/Grid';
import Paper from '@material-ui/core/Paper';
import ButtonBase from '@material-ui/core/ButtonBase';

import CssBaseline from '@material-ui/core/CssBaseline';
import Fab from '@material-ui/core/Fab';
import List from '@material-ui/core/List';
import ListItem from '@material-ui/core/ListItem';
import ListItemAvatar from '@material-ui/core/ListItemAvatar';
import ListItemText from '@material-ui/core/ListItemText';
import ListSubheader from '@material-ui/core/ListSubheader';
import Avatar from '@material-ui/core/Avatar';
import AddIcon from '@material-ui/icons/Add';
import SearchIcon from '@material-ui/icons/Search';
import MoreIcon from '@material-ui/icons/MoreVert';

//LocalStorage Classe:
import LocalStorageHandler from "./LocalStorageHandler";

const useStyles = makeStyles(theme => ({
  root: {
    flexGrow: 1,
  },
  paper: {
    padding: theme.spacing(1),
    margin: 'auto',
    maxWidth: 500,
    minWidth: 500,
  },
  image: {
    width: 128,
    height: "110.129px",
  },
  img: {
    margin: 'auto',
    display: 'block',
    maxWidth: '100%',
    maxHeight: '100%',
  },
  menuButton: {
    marginRight: theme.spacing(1)
  },
  title: {
    flexGrow: 1
  },
  Div:{
  display: 'flex',
  justifyContent: 'center',
  width: '200px',
  margin: '0px',
  border: '1px outset pink'
}

}

));

const Carrinho = props => {
  
  const classes = useStyles();

  const [data, setData] = useState([]);

  function onConfirmar() {
    alert("NÃ£o implementado");
  }

  useEffect(() => {
    setData(JSON.parse(localStorage.getItem("products")));
  }, []);

  if (!data) {
    return <><b>NÃ£o hÃ¡ produtos ainda</b></>;
  }
	
  const remover = (id) =>{
    LocalStorageHandler.removeByIdInArray(id, "products");
    setData(JSON.parse(localStorage.getItem("products")));
  }

	let valorTotal = 0;
	let Conteudo = data.map((produto, _k) => {
    let quant = produto.quantidade;
    let valTotal = produto.preco * quant;
    valorTotal += valTotal;
    return (
      <React.Fragment key={_k}>
      <Paper className={classes.paper}>
        <Grid container spacing={1}>
          <Grid item>
            <ButtonBase className={classes.image}>
              <img className={classes.img} alt="complex" src={produto.imagem} />
            </ButtonBase>
          </Grid>
          <Grid item xs={12} sm container>
            <Grid item xs container direction="column" spacing={1}>
              <Grid item xs>
                <Typography gutterBottom variant="subtitle1">
                <b>{produto.nome}</b>
                </Typography>
                <Typography variant="body2" gutterBottom>
                  {/*DescriÃ§Ã£o aqui*/}
                </Typography>
              </Grid>

	<Grid item>
                <Typography 
	onClick={(ev)=>remover(produto.productId)} size="small"
variant="body2" style={{ cursor: 'pointer' }} color="primary" title="Clique para remover este item do carrinho">
                  Remover
                </Typography>
              </Grid>

            </Grid>
            <Grid item>
			  <Typography variant="subtitle1"><b>Quant:  {quant}</b></Typography>
              <Typography variant="subtitle1"><b>R$  {produto.preco.toFixed(2)}</b></Typography>
              <Typography variant="subtitle1" title="Valor Total"><b>V.T.: R$  {valTotal.toFixed(2)}</b></Typography>
            </Grid>
          </Grid>
        </Grid>
      </Paper>
  <hr style={{ border: 0, borderTop: "1px solid #ddd" }} />
      </React.Fragment>
    );
  });

  return (
  <React.Fragment>
      <CssBaseline />
      <Paper square className={classes.paper}>
        <Typography className={classes.text} variant="h5" gutterBottom>
          Inbox
        </Typography>
        <List className={classes.list}>
          {messages.map(({ id, primary, secondary, person }) => (
            <React.Fragment key={id}>
              {id === 1 && <ListSubheader className={classes.subheader}>Today</ListSubheader>}
              {id === 3 && <ListSubheader className={classes.subheader}>Yesterday</ListSubheader>}
              <ListItem button>
                <ListItemAvatar>
                  <Avatar alt="Profile Picture" src={person} />
                </ListItemAvatar>
                <ListItemText primary={primary} secondary={secondary} />
              </ListItem>
            </React.Fragment>
          ))}
        </List>
      </Paper>
      <AppBar position="fixed" color="primary" className={classes.appBar}>
        <Toolbar>
          <IconButton edge="start" color="inherit" aria-label="open drawer">
            <MenuIcon />
          </IconButton>
          <Fab color="secondary" aria-label="add" className={classes.fabButton}>
            <AddIcon />
          </Fab>
          <div className={classes.grow} />
          <IconButton color="inherit">
            <SearchIcon />
          </IconButton>
          <IconButton edge="end" color="inherit">
            <MoreIcon />
          </IconButton>
        </Toolbar>
      </AppBar>
    </React.Fragment>
  );
};

export default Carrinho;
