 */

const Main = props => {

  const [anchorEl, setAnchorEl] = useState(null);

  const [mobileMoreAnchorEl, setMobileMoreAnchorEl] = useState(null);

  const [countPedidosLocal, setCountPedidosLocal] = useState(0);

  const [data, setData] = useState([]);

  const classes = useStyles();

  const isMenuOpen = Boolean(anchorEl);

  function onAtualizarCount() {
    setCountPedidosLocal(LocalStorageHandler.count("products"));
  }

  useEffect(() => {
    setData(JSON.parse(localStorage.getItem("products")));
    onAtualizarCount();
  });

  function handleProfileMenuOpen(event) {
    onAtualizarCount();
    if (LocalStorageHandler.count("products") > 0) {
      setAnchorEl(event.currentTarget);
    }
  }

  function handleMobileMenuClose() {
    setMobileMoreAnchorEl(null);
  }

  function handleMenuClose() {
    onAtualizarCount();
    setAnchorEl(null);
    handleMobileMenuClose();
  }

  return (
    <React.Fragment>
      <CssBaseline />
      {/* BARRA DE MENU SUPERIOR */}
      <div className={classes.SiteSearchPanel__Background} />
      <AppBar
        position="fixed"
        color="default"
        elevation={0}
        className={classes.appBar}
      >
        <Toolbar className={classes.toolbar}>
          <Typography
            variant="h6"
            color="textPrimary"
            noWrap
            className={classes.toolbarTitle}
          >
            Delivery
          </Typography>
          <Button
            href="#"
            color="primary"
            variant="outlined"
            className={classes.link}
          >
            Entrar
          </Button>
          <IconButton
            onClick={handleProfileMenuOpen}
            aria-label="Account of current user"
            aria-controls="menu-appbar"
            aria-haspopup="true"
            color="inherit"
          >
            <Badge
              className={classes.margin}
              badgeContent={countPedidosLocal}
              color="primary"
            >
              <ShoppingCartOutlined />
            </Badge>
          </IconButton>
        </Toolbar>
      </AppBar>
      {/* Hero unit */}
      <Container maxWidth="sm" component="main" className={classes.heroContent}>
        <br />
        <Typography
          component="h4"
          variant="h4"
          align="center"
          color="textPrimary"
          gutterBottom
        >
          Alguma mensagem impactante aqui
        </Typography><br/>
        {/* INPUT */}
        <Paper className={classes.rootinput}>
          <IconButton className={classes.iconButton} aria-label="Menu">
            <Menu />
          </IconButton>
          <InputBase
            className={classes.textFieldInput}
            placeholder="Pesquisar"
            inputProps={{ "aria-label": "Search Google Maps" }}
          />
          <Divider className={classes.divider} />
          <IconButton className={classes.iconButton} aria-label="Search">
            <Search />
          </IconButton>
        </Paper>
        <Typography
          variant="h5"
          align="center"
          color="textSecondary"
          component="p"
        /><br/>
      </Container>
      {/* End hero unit */}
      <Container className={classes.cardGrid}  maxWidth="md">
        <AppBar
          position="static"
          color="default"
          elevation={0}
          className={classes.appBar}
        >
          <Toolbar className={classes.toolbar2}>
            <Typography
              variant="h6"
              color="textPrimary"
              noWrap
              className={classes.toolbarTitle}
            />
            <IconButton
              aria-label="Account of current user"
              aria-controls="menu-appbar"
              aria-haspopup="true"
              color="primary"
            >
              <GridOn />
            </IconButton>
            <Divider className={classes.divider} />
            <IconButton
              aria-label="Account of current user"
              aria-controls="menu-appbar"
              aria-haspopup="true"
              color="primary"
            >
              <ReorderRounded />
            </IconButton>
          </Toolbar>
        </AppBar>
        <br />
        {/* End hero unit */}
        {MostrarEmpresas(props, classes)}
      </Container>
      {/* MenuFooter */}
      <Footer footer="" />
      {/* End footer */}
      <MyMenu
        anchorEl={anchorEl}
        data={data}
        handleMenuClose={handleMenuClose}
        abrir={isMenuOpen}
      />
    </React.Fragment>
  );
};

export default Main;
