/**
 * Component: Produtos
 */
function Produto(props) {
	
	const [quantidade, setQuantidade] = useState(0);
	const [valorTotal, setValorTotal] = useState(0);
	const [descricao, setDescricao] = useState('');
	const [observacao, setObservacao] = useState('');

	function removeProduct(id) {
		let storageProducts = JSON.parse(localStorage.getItem('products'));
		let products = storageProducts.filter(product => product.id !== id);
		localStorage.setItem('products', JSON.stringify(products));
	}

	function addProduct() {
		let products = [];

		if (localStorage.getItem('products')) {
			products = JSON.parse(localStorage.getItem('products'));
		}

		//Desestruturação Javascript
		const { id, nome, imagem, precoUnitario, descricao } = props;

		//Pega o index
		let objIndex = products.findIndex(pedido => pedido.productId === id);

		//Caso não exista
		if (objIndex === -1) {
			products.push({
				productId: id,
				nome: nome,
				imagem: imagem,
				quantidade: quantidade + 1,
				observacao: observacao,
				preco: precoUnitario,
				descricao: descricao,
			});

			localStorage.setItem('products', JSON.stringify(products));
		} else {
			products[objIndex].productId = id;
			products[objIndex].quantidade = quantidade + 1;
			products[objIndex].observacao = observacao;
			products[objIndex].nome = nome;
			products[objIndex].imagem = imagem;
			products[objIndex].preco = precoUnitario;
			products[objIndex].descricao = descricao;
			localStorage.setItem('products', JSON.stringify(products));
		}
		
	}

	function add() {
		let quant = parseInt(quantidade) + 1;

		setQuantidade(quant);

		//props.handleTotal(props.precoUnitario);

		addProduct();

		setValorTotal((props.precoUnitario * quant).toFixed(2));
	}

	function subtract() {
		
		let quant = parseInt(quantidade) - 1;

		setQuantidade(quant);

		addProduct();

		//props.handleTotal(-props.precoUnitario);

		setValorTotal(props.precoUnitario * quant);
	}

	function onChangeQuantidade(ev) {
		let quant = parseInt(ev.target.value);

		setQuantidade(quant);

		//props.handleTotal(props.precoUnitario);

		addProduct();

		setValorTotal(props.precoUnitario * parseInt(quant));
	}

	function alterarTextoTextArea(ev) {
		setObservacao(ev.target.value);
	}

	const classes = useStyles();

	return (
		<div className={classes.root} id={props.id}>
			<Grid container spacing={2}>
				<Grid item xs={12} md={12}>
					<div className={classes.demo}>
						<div className={classes.listaProdutos} title={'Clique para entrar em ' + props.nome}>
							<div className={classes.itemAvatar}>
								<img className={classes.itemAvatarImg} alt="" src={props.imagem} />
							</div>
							<div className={classes.itemContent}>
								<Typography>
									<b>{props.nome}</b>
								</Typography>
								<Typography>
									<span>{props.descricao}</span>
								</Typography>
								<sub>
									<strong>Kg</strong>
								</sub>
								<FormControl>
									<InputLabel htmlFor="my-input">Observação:</InputLabel>
									<Input
										id="my-input"
										onChange={ev => {
											alterarTextoTextArea(ev);
										}}
										value={observacao}
										aria-describedby="my-helper-text"
									/>
								</FormControl>
							</div>
							<div className={classes.itemAcoes}>
								<br />
								<Typography>
									Preço <br /> <b>R$ {props.precoUnitario}</b>
								</Typography>
								<small>Quantidade:</small>
								<ButtonGroup variant="contained" color="primary" aria-label="Split button">
									<Button onClick={subtract} disabled={quantidade < 1}>
										-
									</Button>
									<input
										type="number"
										onChange={ev => onChangeQuantidade(ev)}
										value={quantidade}
										className={classes.inputQuantidade}
									/>
									<Button
										color="primary"
										variant="contained"
										size="small"
										aria-haspopup="true"
										value="+"
										onClick={add}
									>
										+
									</Button>
								</ButtonGroup>
								<br />
								<Chip
									className={classes.chip}
									color="primary"
									label={`Valor a pagar${'\n\n'}R$ ${valorTotal}`}
								/>
								<br />
							</div>
						</div>
					</div>
				</Grid>
			</Grid>
		</div>
	);
}

const Index = props => {
	
	const router = useRouter();

	const [value, setValue] = React.useState(0);
	
	const [produtosAsObject, setProdutosAsObject] = React.useState([]);
	
	const [search, setSearch] = React.useState("");
	
	const [countPedidosLocal, setCountPedidosLocal] = useState(!1);
	
	const [anchorEl, setAnchorEl] = useState(null);
	
	const [mobileMoreAnchorEl, setMobileMoreAnchorEl] = useState(null);

	const isMenuOpen = Boolean(anchorEl);

	const isMobileMenuOpen = Boolean(mobileMoreAnchorEl);
	
	const classes = useStyles();

	useEffect(() => {
		onAtualizarCount();
		
		setProdutosAsObject(props.shows);
		
		
		let adults = LocalStorageHandler.getDataByKey('products').filter(produto => produto.nome.toUpperCase() == search.toUpperCase());
		
		console.log(adults);
		
		
		return () => {
			// Limpa a assinatura antes do componente deixar a tela
			//subscription.unsubscribe();
		};
	});

	function onAtualizarCount() {
		setCountPedidosLocal(LocalStorageHandler.count('products'));
	}

	function handleChange(event, newValue) {
		setValue(newValue);
	}

	function handleProfileMenuOpen(event) {
		onAtualizarCount();
		setAnchorEl(event.currentTarget);
	}

	function handleMobileMenuClose() {
		setMobileMoreAnchorEl(null);
	}

	function handleMenuClose() {
		onAtualizarCount();
		setAnchorEl(null);
		handleMobileMenuClose();
  }

	function handleMobileMenuOpen(event) {
		setMobileMoreAnchorEl(event.currentTarget);
	}
	
	return (
		<React.Fragment>
			<CssBaseline />
			{/* MENU DE BARRA SUPERIOR */}
			<AppBar position="fixed" color="default" elevation={0} className={classes.appBar}>
				<Toolbar className={classes.toolbar}>
					<Typography variant="h6" color="inherit" noWrap className={classes.toolbarTitle}>
						<Link href="../">Delivery {router.query.id}</Link>
					</Typography>
					<Button href="#" color="primary" variant="outlined" className={classes.link}>
						Sair
					</Button>
					<IconButton
						onClick={handleProfileMenuOpen}
						aria-label="Account of current user"
						aria-controls="menu-appbar"
						aria-haspopup="true"
						color="inherit"
					>
						<Badge className={classes.margin} badgeContent={countPedidosLocal} color="primary">
							<ShoppingCartOutlined />
						</Badge>
					</IconButton>
				</Toolbar>
			</AppBar>
			<Container className={classes.cardGrid} maxWidth="md">
				<br />
				{/* INÍCIO DO TAB CONTENT */}
				<AppBar position="static">
					<Tabs value={value} onChange={handleChange} aria-label="Simple tabs example">
						<Tab label="PRODUTOS" {...a11yProps(0)} />
						<Tab label="SEUS PEDIDOS" {...a11yProps(1)} />
						<Tab label="AVALIAÇÕES" {...a11yProps(2)} />
					</Tabs>
				</AppBar>
				{/* TAB 1 */}
				<TabPanel value={value} index={0}>
					<Container maxWidth="sm" component="main" className={classes.heroContent}>
						{/* INPUT */}
						
						<Paper className={classes.rootinput}>
							<IconButton className={classes.iconButton} aria-label="Menu">
								<Menu />
							</IconButton>
							<InputBase
								className={classes.textFieldInput}
								placeholder={`Pesquise produtos aqui...`}
								onChange={(ev)=>setSearch(ev.target.value)}
								value={search}
								inputProps={{ 'aria-label': 'Search Google Maps' }}
							/>
							<Divider className={classes.divider} />
							<IconButton className={classes.iconButton} aria-label="Search">
								<Search />
							</IconButton>
						</Paper>
						<Typography variant="h5" align="center" color="textSecondary" component="p">
							<br />
						</Typography>
					</Container>
					{Object.values(produtosAsObject).map((product, _key) => {
						if (product.id != undefined) {
							return (
								<React.Fragment key={_key}>
									<Produto
										produto={product}
										id={product.id}
										nome={product.nome.toUpperCase()}
										imagem={product.imagem}
										precoUnitario={product.preco}
										descricao={product.descricao}
									/>
								</React.Fragment>
							);
						}
						/**/
					})}
				</TabPanel>
				{/* TAB 2 */}
				<TabPanel value={value} index={1}>
					{/*<Carrinho />*/}
				</TabPanel>
				{/* TAB 3 */}
				<TabPanel value={value} index={2}>
					Tab 3
				</TabPanel>
			</Container>
			{/* Footer */}
			<Container maxWidth="md" component="footer" className={classes.footer}>
				<Grid container spacing={4} justify="space-evenly">
					{Footers.map(footer => (
						<Grid item xs={6} sm={3} key={footer.title}>
							<Typography variant="h6" color="textPrimary" gutterBottom>
								{footer.title}
							</Typography>
							<ul>
								{footer.description.map(item => (
									<li key={item}>
										<Link href="#" variant="subtitle1" color="textSecondary">
											{item}
										</Link>
									</li>
								))}
							</ul>
						</Grid>
					))}
				</Grid>
				<Box mt={5}>{MadeWithLove(props)}</Box>
			</Container>
			{/* End footer */}
			<MyMenu anchorEl={anchorEl} handleMenuClose={handleMenuClose} abrir={isMenuOpen} />
		</React.Fragment>
	);
};

Index.getInitialProps = async function(ctx) {
	const res = await import('../../db/produtos.json');
	const configSite = await import('../../db/config.dev.json');

	return {
		shows: res,
		config: configSite,
	};
};

export default Index;
