import React, { useState, Fragment } from "react";
import Card from "@material-ui/core/Card";
import CardActions from "@material-ui/core/CardActions";
import CardContent from "@material-ui/core/CardContent";
import CardMedia from "@material-ui/core/CardMedia";
import { makeStyles } from "@material-ui/core/styles";
import Skeleton from "@material-ui/lab/Skeleton";
import Avatar from "@material-ui/core/Avatar";
import Grid from "@material-ui/core/Grid";
import Button from "@material-ui/core/Button";
import IconButton from "@material-ui/core/IconButton";
import AddIcon from "@material-ui/icons/Add";
import DeleteIcon from "@material-ui/icons/Delete";
import AddCircleOutlineIcon from "@material-ui/icons/AddCircleOutline";
import RemoveCircleOutlineRoundedIcon from "@material-ui/icons/RemoveCircleOutlineRounded";
import LocalStorageHandler from "../LocalStorageHandler";

const useStyles2 = makeStyles(theme => ({
  root: {
    ...theme.typography.button,
    backgroundColor: theme.palette.background.paper,
    padding: theme.spacing(1),
    textAlign: "center"
  },
  cardMedia: {
    backgroundColor: "transparent",
    margin: 15,
    height:200
  },
  cardGrid: {
    paddingTop: theme.spacing(8),
    paddingBottom: theme.spacing(8)
  },
  card: {
    height: "100%",
    display: "flex",
    flexDirection: "column"
  }
}));


//values: { xs: 0, sm: 600, md: 960, lg: 1280, xl: 1920 },
const useStyles = makeStyles(theme => ({
  root: {
    border: "0px solid #ccc",
    marginBottom: 4,
    boxShadow: theme.shadows[3],
    backgroundColor: "rgba(245,245,245,0.8)"
  },
  imageBox: {
    width: "100%",
    height: 140,
    flex: 1,
    [theme.breakpoints.up("xs")]: {
      border: "0px solid green"
    },
    [theme.breakpoints.up("sm")]: {
      border: "0px solid red"
    },
    [theme.breakpoints.up("md")]: {
      border: "0px solid lime"
    },
    [theme.breakpoints.up("lg")]: {
      border: "0px solid gold"
    }
  },
  bigAvatar: {
    height: 100,
    borderRadius: 5,
    [theme.breakpoints.up("xs")]: {
      border: "1px solid green",
      margin: 0,
      width: 80
    },
    [theme.breakpoints.up("sm")]: {
      margin: 10,
      width: 100
    },
    [theme.breakpoints.up("md")]: {
      margin: 10,
      width: 100
    },
    [theme.breakpoints.up("lg")]: {
      margin: 10,
      width: 100
    }
  },
  quantidade: {
    textAlign: "right",
    width: 40,
    paddingTop: 5,
    paddingBottom: 5,
    display: "inline",
    border: "0",
    borderBottom: "1px solid #ccc",
position:'relative',
top:-4
  },
  cardAvatar: {
    border: "1px solid red"
  },
  cardContent: {
    border: "1px solid lime"
  },
  cardActions: {
    border: "1px solid blue"
  },
  valorUnitario: {
    border: "0px solid red",
    color: "rgb(244,67,54)",
    [theme.breakpoints.up("xs")]: {
      fontWeight: "bold"
    }
  },
  subtotal: {
    border: "0px solid red",
    paddingRight: 12,
    fontSize: "12px"
  },
  mdlCard: {
    flexDirection: "column",
    fontSize: "16px",
    fontWeight: 400,
    overflow: "hidden",
    zIndex: 1,
    position: "relative",
    background: "#fff",
    borderRadius: "2px",
    boxSizing: "border-box",
    border: "0px solid red"
  },
  cardItemMenu: {
    [theme.breakpoints.up("xs")]: {
      width: "100%",
    },
    [theme.breakpoints.up("sm")]: {
      width: "96%",
    },
    [theme.breakpoints.up("md")]: {
      width: "96%",
    },
    [theme.breakpoints.up("lg")]: {
      width: "96%",
    },
    margin: "10px auto",
    minHeight: "10px",
    border: "0px solid red"
  },
  info_item: {
    float: 'none',
  },
  nomeItemMenu: {
    fontFamily: "Lato",
    color: "#444",
    fontSize: "18px",
    margin: "0",
    padding: "0",
    marginTop: "0px",
    verticalAlign: "baseline",
    textTransform: "capitalize",
    fontWeight: "bold",
    lineHeight:"35px"
  },
  descItemMenu: {
    fontFamily: "Lato",
    color: "#444",
    fontSize: "12px",
    margin: 0,
    padding: 0,
    marginTop: "0px",
    verticalAlign: "baseline",
    lineHeight: "14px",
    textTransform: "capitalize"
  },
  img_prod: {
    width: "80px",
    height: "80px",
    float: "left",
    margin: "10px",
    borderRadius: "5px",
    maxWidth: "100%"
  },
  mdlCard__actions: {
    fontSize: "12px",
    lineHeight: "normal",
    width: "100%",
    backgroundColor: "transparent",
    padding: "8px",
    boxSizing: "border-box",
    borderTop: "1px solid rgba(0,0,0,.1)"
  },
  myButton: {
    color: "rgb(244,67,54)",
    border: "none",
    borderRadius: "2px",
    display: "block",
    width: 40,
    minWidth: "30px"
  }
}));

/**
 * Component: Produtos
 */
export default function Produtos(props) {
  const classes = useStyles();

  return (
    <>
      <div className={classes.cardItemMenu}>
        <div className={classes.mdlCard}>
          <div className={classes.info_item}>
            <Avatar
              alt="Remy Sharp"
              src={props.imagem}
              className={classes.img_prod}
            />
            <CardMedia
                className={classes.cardMedia}
              >
                <Skeleton height="100%" width="100%" />
              </CardMedia>
            <p className={classes.nomeItemMenu}>{props.nome}</p>
            <p className={classes.descItemMenu}>
              {props.descricao.length == 0 ? (
                <>
                  {props.nome}
                </>
              ) : (
                <>{props.descricao}</>
              )}
            </p>
          </div>
          <Grid container className={classes.mdlCard__actions}>
            <Grid item xs="7">
              <Button className={classes.valorUnitario}>
                <b>
                  R${" "}
                  {props.precoUnitario != undefined
                    ? props.precoUnitario.toFixed(2)
                    : null}
                </b>
              </Button>{quantidade > 0 && <>Subtotal: R$ {valorTotal}</>}
            </Grid>
            <Grid
              container
              direction="row"
              justify="flex-end"
              alignItems="center"
              xs="5"
            >
              <Button 
              onClick={subtract}
              disabled={quantidade < 1}
              className={classes.myButton}>
                <RemoveCircleOutlineRoundedIcon />
              </Button>
              <input
                type="number"
                onChange={ev => onChangeQuantidade(ev)}
                value={quantidade}
                className={classes.quantidade}
              />
              <Button className={classes.myButton} onClick={add}>
                <AddCircleOutlineIcon />
              </Button>
            </Grid>
          </Grid>
        </div>
      </div>
    </>
  );
}
