import React, { useState, useEffect, createRef } from "react";
import styled from "styled-components";

import { makeStyles } from "@material-ui/core/styles";
import AppBar from "@material-ui/core/AppBar";
import Toolbar from "@material-ui/core/Toolbar";
import Typography from "@material-ui/core/Typography";
import Button from "@material-ui/core/Button";
import IconButton from "@material-ui/core/IconButton";
import MenuIcon from "@material-ui/icons/Menu";


import Grid from '@material-ui/core/Grid';
import Paper from '@material-ui/core/Paper';
import ButtonBase from '@material-ui/core/ButtonBase';


const useStyles = makeStyles(theme => ({
  root: {
    flexGrow: 1,
  },
  paper: {
    padding: theme.spacing(2),
    margin: 'auto',
    maxWidth: 500,
  },
  image: {
    width: 128,
    height: 128,
  },
  img: {
    margin: 'auto',
    display: 'block',
    maxWidth: '100%',
    maxHeight: '100%',
  },
  menuButton: {
    marginRight: theme.spacing(2)
  },
  title: {
    flexGrow: 1
  },
  Div:{
  display: 'flex',
  justifyContent: 'center',
  width: '200px',
  margin: '0px',
  border: '1px outset pink'
}

}

));

const Div = styled.div`
  display: flex;
  justify-content: center;
  width: 300px;
  margin: 0px;
  border: 0px outset pink;
  &:hover {
    background-color: #ccc;
  }
`;

const Carrinho = props => {
  const classes = useStyles();

  const [data, setData] = useState([]);

  function onConfirmar() {
    alert("NÃ£o implementado");
  }

  useEffect(() => {
    setData(JSON.parse(localStorage.getItem("products")));
  }, []);

  if (!data) {
    return <>NÃ£o hÃ¡ produtos ainda</>;
  }
  let valorTotal = 0;
  let Conteudo = data.map((produto, _k) => {
    let quant = produto.quantidade;
    let valTotal = produto.preco * quant;
    valorTotal += valTotal;
    return (
      <React.Fragment key={_k}>
      <Paper className={classes.paper}>
        <Grid container spacing={1}>
          <Grid item>
            <ButtonBase className={classes.image}>
              <img className={classes.img} alt="complex" src={produto.imagem} />
            </ButtonBase>
          </Grid>
          <Grid item xs={12} sm container>
            <Grid item xs container direction="column" spacing={1}>
              <Grid item xs>
                <Typography gutterBottom variant="subtitle1">
                {produto.nome}
                </Typography>
                <Typography variant="body2" gutterBottom>
                  {/*DescriÃ§Ã£o aqui*/}
                </Typography>
              </Grid>

	<Grid item>
                <Typography variant="body2" style={{ cursor: 'pointer' }}>
                  Remove
                </Typography>
              </Grid>

            </Grid>
            <Grid item>
              <Typography variant="subtitle1">R$ {produto.preco.toFixed(2)}</Typography>
              <Typography variant="subtitle1">R$ {valTotal.toFixed(2)}</Typography>
            </Grid>
          </Grid>
        </Grid>
      </Paper>
  <hr style={{ border: 0, borderTop: "1px solid #ddd" }} />
      </React.Fragment>
    );
  });

  return (
    <React.Fragment>
      {Conteudo}
    </React.Fragment>
  );
};

export default Carrinho;
