import React, { useState, useEffect, createRef }  from "react";
import styled from 'styled-components';


const Div = styled.div`
  display: flex;
  justify-content: center;
  width:400px;
  margin: 0px;
  border: 0px outset pink;
  &:hover {
   background-color: #ccc;
 }`;
 
const cssMedia = styled.div`
  width:20%;
  margin: 0px;
  border: 3px outset pink;
  &:hover {
   background-color: #ccc;
 }`;
 
 const Content = styled.div`
  width:60%;
  margin: 0px;
  border: 3px outset pink;
  &:hover {
   background-color: #ccc;
 }`;
 
const cssActions = styled.div`
  width:20%;
  margin: 0px;
  border: 3px outset pink;
  &:hover {
   background-color: #ccc;
 }`;

const Carrinho  = props =>{
	
	const [data, setData] = useState([]);
	
	 useEffect(() => {
		 setData(JSON.parse(localStorage.getItem('products')));
	 }, []);
	
	if(!data){
		return(
			<>
			Não há produtos ainda
			</>
		)
	}
	
	let d = data.map((produto, _k)=>{
		return <React.Fragment key={_k}>
		<Div>
			<cssMedia>
				<img src="https://material-ui.com/static/images/grid-list/morning.jpg"
				style={{width:"100%", height:"100%"}}
				alt="https://material-ui.com/static/images/grid-list/morning.jpg" />
			</cssMedia>	
			<Content>
				<h4>{produto.productId}</h4>
			</Content>	
			<cssActions>
				<h4>{produto.quantidade}</h4>
			</cssActions>
		</Div>
		<hr style={{border:0,borderTop:"1px solid #ddd"}}/>
		</React.Fragment>;
	});
	
	return(
		<>
		{d}
		</>
	)
}


export default Carrinho;
